import React, { useState } from "react";
import '../styles/index.css'
import Navbar from "./Navbar";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from "react-router-dom";

const PharmacyRegister = () => {

    const [pharmacyName, setPharmacyName] = useState("");
    const [address, setAddress] = useState("");
    const [pharmacyOwnerName, setPharmacyOwnerName] = useState("");
    const [adhaarNo, setAdhaarNo] = useState("");
    const [panNo, setPanNo] = useState("");
    const [email, setEmail] = useState("");
    const [contact, setContact] = useState("");
    const [GSTNo, setGSTNo] = useState("");
    const [licenseNo, setLicenseNo] = useState("");
    const [license, setLicense] = useState("");
    
    // var acc = document.getElementsByClassName("accordion");
    // var i;

    // for (i = 0; i < acc.length; i++) {
    //     acc[i].addEventListener("click", function () {
    //         /* Toggle between adding and removing the "active" class,
    //         to highlight the button that controls the panel */
    //         this.classList.toggle("active");

    //         /* Toggle between hiding and showing the active panel */
    //         var panel = this.nextElementSibling;
    //         if (panel.style.display === "block") {
    //             panel.style.display = "none";
    //         } else {
    //             panel.style.display = "block";
    //         }
    //     });
    // }

    const collectData = async (e) => {
        e.preventDefault();
        
        //console.warn(name, password)
      
        let result = await fetch("http://localhost:9000/mplusm/pharmacist/signup", {
            method: 'post',
            body: JSON.stringify({pharmacyName,address,pharmacyOwnerName,adhaarNo,panNo,email,contact,GSTNo,licenseNo,license}),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        
        result = await result.blob();
        
    }
//     const [fileData, setFileData] = useState("");
// const getFile = (e) => {
//   setFileData(e.target.files[0]);
// };
//     const uploadFile = async (e) => { 
//         e.preventDefault();   
//         const data = new FormData();
//         data.append("file", fileData);
//         let result=await fetch({
//           method: "POST",
//           url: "http://localhost:9000/mplusm/pharmacist/uploads",
//           data: data,
//         }).then((res) => {       
//             alert(res.data.message);
//         });
//       };
    return (
        <div>
            <Navbar />
            <div >
                <div class="card">
                    <h5 class="card-title">Register Your Pharmacy</h5>
                    <div class="card-body pt-3">
                        <form>
                            <div class="row mb-3">
                                <label for="PharmacyName" class="col-md-4 col-lg-3 col-form-label">Pharmacy Name</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyName" type="text" class="form-control" id="PharmacyName" onChange={(e) => setPharmacyName(e.target.value)} />
                                </div>
                            </div>

                            <div class="row mb-3">
                                <label for="PharmacyAddress" class="col-md-4 col-lg-3 col-form-label">Pharmacy Address</label>
                                <div class="col-md-8 col-lg-9">
                                    <textarea name="PharmacyAddress" type="text" class="form-control" id="PharmacyAddress" onChange={(e) => setAddress(e.target.value)} />
                                </div>
                            </div>

                            {/* <div class="accordion" id="accordion">
                            <div class="accordion-item">
                                <h2 class="accordion-header" id="PharmacyOwner">
                                    <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                                        Pharmacy Owner details
                                    </button>
                                </h2>
                                <div id="collapseOne" class="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                                    <div class="accordion-body">
                                        <strong>This is the first item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                                    </div>
                                </div>
                            </div>
                            <div class="accordion-item">
                                <h2 class="accordion-header" id="headingTwo">
                                    <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo">
                                        Accordion Item #2
                                    </button>
                                </h2>
                                <div id="collapseTwo" class="accordion-collapse collapse" aria-labelledby="headingTwo" data-bs-parent="#accordionExample">
                                    <div class="accordion-body">
                                        <strong>This is the second item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                                    </div>
                                </div>
                            </div>
                        </div> */}

                            <div class="row mb-3">
                                <label for="PharmacyOwnerName" class="col-md-4 col-lg-3 col-form-label">Pharmacy Owner Name</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyOwnerName" type="text" class="form-control" id="PharmacyOwnerName" onChange={(e) => setPharmacyOwnerName(e.target.value)} />
                                </div>
                            </div>

                            {/* <div class="row mb-3">
                            <label for="profileImage" class="col-md-4 col-lg-3 col-form-label">Profile Image</label>
                            <div class="col-md-8 col-lg-9">
                                <img src="assets/img/profile-img.jpg" alt="Profile" />
                                <div class="pt-2">
                                    <a href="#" class="btn btn-primary btn-sm" title="Upload new profile image"><i class="bi bi-upload"></i></a>
                                    <a href="#" class="btn btn-danger btn-sm" title="Remove my profile image"><i class="bi bi-trash"></i></a>
                                </div>
                            </div>
                        </div> */}


                            <div class="row mb-3">
                                <label for="PharmacyOwnerAdhaar" class="col-md-4 col-lg-3 col-form-label">Pharmacy Owner Adhaar No.</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyOwnerAdhaar" type="text" class="form-control" id="PharmacyOwnerAdhaar" onChange={(e) => setAdhaarNo(e.target.value)} />
                                </div>
                            </div>

                            <div class="row mb-3">
                                <label for="PharmacyOwnerPan" class="col-md-4 col-lg-3 col-form-label">Pharmacy Owner PAN No.</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyOwnerPan" type="text" class="form-control" id="PharmacyOwnerPan" onChange={(e) => setPanNo(e.target.value)} />
                                </div>
                            </div>

                            <div class="row mb-3">
                                <label for="PharmacyEmail" class="col-md-4 col-lg-3 col-form-label">Email ID</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyEmail" type="text" class="form-control" id="PharmacyEmail" onChange={(e) => setEmail(e.target.value)} />
                                </div>
                            </div>

                            <div class="row mb-3">
                                <label for="PharmacyContactNo" class="col-md-4 col-lg-3 col-form-label">Contact No.</label>
                                <div class="col-md-8 col-lg-9">
                                    <input name="PharmacyContactNo" type="text" class="form-control" id="PharmacyContactNo" onChange={(e) => setContact(e.target.value)} />
                                </div>
                            </div>

                            <div class="row mb-3">
